safeSetInnerHTML(17, "btb_title", `<div class="title-container"><h1 style="white-space:pre-wrap;">Bags 10-14: The First Level of the Nave and Transept</h1></div>`);
safeSetInnerHTML(17, "btb_icon_1", `<div class="icon-container"><i class="fas fa-church"></i></div><div class="title-container">Layout of Gothic Cathedrals</div><div class="content-container">Nave for congregations, the transept forming a cross, and the chancel the center of religious ceremonies</div>`);
safeSetInnerHTML(17, "btb_icon_2", `<div class="icon-container"><i class="fas fa-archway"></i></div><div class="title-container">Gothic Architectural Features</div><div class="content-container">Highlights verticality and light, blending functionality with the grandeur of Gothic design.</div>`);
hideElement("btb_icon_3", 17);
safeSetInnerHTML(17, "btb_audio", `<div class="audio-player"><a href="#" class="read-to-me-btn" onclick="toggleAudio(event, 'audio17', 'playPauseIcon17', 'progressBar17')"><i id="playPauseIcon17" class="fas fa-play icon"></i>Read to Me</a><input type="range" id="progressBar17" class="progress-bar" value="0" max="100"><audio id="audio17" src="https://behind-the-builds.github.io/btb/notre-dame/audio/notre-dame_bags_10_14.mp3"></audio></div>`);
safeSetInnerHTML(17, "btb_maintext", `<p class="sqsrte-large" style="white-space:pre-wrap;">The <strong>nave</strong>, <strong>transept</strong>, and <strong>chancel</strong> together form the primary structure of Notre-Dame Cathedral, encompassing a significant portion of the church.</p><p class="sqsrte-large" style="white-space:pre-wrap;">The <strong>nave</strong> serves as the central area, designed to accommodate large congregations and featuring multiple aisles that enhance the sense of space. The <strong>transept</strong> intersects with the nave, creating a cross-shaped layout typical of Gothic cathedrals, and is adorned with striking rose windows at each end.</p><p class="sqsrte-large" style="white-space:pre-wrap;">The <strong>chancel</strong>, or choir, extends from the transept and houses the altar, serving as a focal point for worship.</p>`);
safeSetInnerHTML(17, "btb_image", `<img src="https://behind-the-builds.github.io/btb/notre-dame/images/Paris_and_environs,_with_routes_from_London_to_Paris_-_handbook_for_travellers_(1913)_(14594939820).jpg" alt="Notre-Dame de Paris floorplan, from 1913 travel guide">`);
