hideElement("btb_video_embed", 21);
safeSetInnerHTML(22, "btb_title", `<div class="title-container"><h1 style="white-space:pre-wrap;">Instructions 164-172: Nave Pillars and the Western Entrance</h1></div>`);
safeSetInnerHTML(22, "btb_icon_1", `<div class="icon-container"><i class="fas fa-church"></i></div><div class="title-container">Notre-Dame Cathedral Interior</div><div class="content-container">Incorporation of internal supporting pillars in the nave enhances structural integrity and supports vaulted ceilings.</div>`);
safeSetInnerHTML(22, "btb_icon_2", `<div class="icon-container"><i class="fas fa-archway"></i></div><div class="title-container">Cathedral's Main Entrance</div><div class="content-container">The western façade forms a focal point, embodying the grand entrance for visitors.</div>`);
hideElement("btb_icon_3", 22);
safeSetInnerHTML(22, "btb_audio", `<div class="audio-player"><a href="#" class="read-to-me-btn" onclick="toggleAudio(event, 'audio22', 'playPauseIcon22', 'progressBar22')"><i id="playPauseIcon22" class="fas fa-play icon"></i>Read to Me</a><input type="range" id="progressBar22" class="progress-bar" value="0" max="100"><audio id="audio22" src="https://behind-the-builds.github.io/btb/notre-dame/audio/notre-dame_instructions_164_172.mp3"></audio></div>`);
safeSetInnerHTML(22, "btb_maintext", `<p class="sqsrte-large" style="white-space:pre-wrap;">The construction of <strong>internal supporting pillars</strong> in the nave marks a significant step in reinforcing the <strong>structural integrity</strong> of <strong>Notre-Dame Cathedral</strong>. These round pillars, are strategically placed to support the <strong>vaulted ceilings</strong> and create a sense of verticality throughout the nave. The rhythm established by these pillars, along with the arches and pilasters, contributes to the overall harmony of the interior space.</p><p class="sqsrte-large" style="white-space:pre-wrap;">At the western end of the cathedral, the main entrance features an impressive façade that serves as a <strong>focal point</strong> for visitors. We will discuss this in greater detail as we continue to build the western entrance.</p>`);
safeSetInnerHTML(22, "btb_image", `<img src="https://behind-the-builds.github.io/btb/notre-dame/images/Notre_Dame_nave_1,_Paris_June_2014.jpg" alt="Notre-Dame nave looking east toward the chancel">`);
safeSetInnerHTML(22, "btb_caption", `<div class="caption-container" style="text-align: center;"><p class="sqsrte-medium" style="white-space:pre-wrap;">Notre-Dame nave looking east toward the chancel</p></div><div class="source-container" style="text-align: center;"><p class="sqsrte-medium" style="white-space:pre-wrap;"><a href="https://commons.wikimedia.org/wiki/File:Notre_Dame_nave_1,_Paris_June_2014.jpg" target="_blank" style="color: #0073e6; text-decoration: underline;">source</a></p></div>`);
