safeSetInnerHTML(30, "btb_title", `<div class="title-container"><h1 style="white-space:pre-wrap;">Instructions 225-231: Clerestory Arches and Windows of the Nave</h1></div>`);
safeSetInnerHTML(30, "btb_icon_1", `<div class="icon-container"><i class="fas fa-church"></i></div><div class="title-container">Notre-Dame Clerestory</div><div class="content-container">Features tall, pointed arches with large windows, flooding the nave with natural light and enhancing the ethereal atmosphere.</div>`);
safeSetInnerHTML(30, "btb_icon_2", `<div class="icon-container"><i class="fas fa-palette"></i></div><div class="title-container">Stained Glass Artistry</div><div class="content-container">The clerestory windows are adorned with intricate stained glass, depicting religious scenes that add vibrant colors inside.</div>`);
hideElement("btb_icon_3", 30);
safeSetInnerHTML(30, "btb_audio", `<div class="audio-player"><a href="#" class="read-to-me-btn" onclick="toggleAudio(event, 'audio30', 'playPauseIcon30', 'progressBar30')"><i id="playPauseIcon30" class="fas fa-play icon"></i>Read to Me</a><input type="range" id="progressBar30" class="progress-bar" value="0" max="100"><audio id="audio30" src="https://behind-the-builds.github.io/btb/notre-dame/audio/notre-dame_instructions_225_231.mp3"></audio></div>`);
safeSetInnerHTML(30, "btb_maintext", `<p class="sqsrte-large" style="white-space:pre-wrap;">The <strong>clerestory level</strong> of Notre-Dame's nave features tall, pointed arches that support the <strong>vaulted ceiling</strong>. Within these arches are large windows designed to flood the interior with <strong>natural light</strong>. Each bay typically includes a pair of narrow, pointed lancet windows arranged side-by-side, complemented by a small rose window above them, adding both visual interest and illumination to the space.</p><p class="sqsrte-large" style="white-space:pre-wrap;">These clerestory windows are adorned with intricate <strong>stained glass</strong>, depicting various religious scenes and figures. As sunlight filters through, the colored glass creates a vibrant, <strong>ethereal atmosphere</strong> inside the cathedral.</p>`);
safeSetInnerHTML(30, "btb_image", `<img src="https://behind-the-builds.github.io/btb/notre-dame/images/Cathédrale_Notre_Dame_de_Paris_(22460169162).jpg" alt="Cathédrale Notre Dame de Paris">`);
safeSetInnerHTML(30, "btb_caption", `<div class="caption-container" style="text-align: center;"><p class="sqsrte-medium" style="white-space:pre-wrap;">Cathédrale Notre Dame de Paris</p></div><div class="source-container" style="text-align: center;"><p class="sqsrte-medium" style="white-space:pre-wrap;"><a href="https://commons.wikimedia.org/wiki/File:Cath%C3%A9drale_Notre_Dame_de_Paris_(22460169162).jpg" target="_blank" style="color: #0073e6; text-decoration: underline;">source</a></p></div>`);
hideElement("btb_video_header", 30);
